import firebase from "firebase/compat";

const saveTranscription = (userGmail,textInput) => {
    const saveToFirebase = firebase.firestore();
    saveToFirebase.collection("transcription").add({
      email: userGmail,
      text: textInput
    });
  };

export default saveTranscription;
