import {Recorder} from 'react-voice-recorder'
import 'react-voice-recorder/dist/index.css'
import {useState} from "react";
import React from "react";



function RecorderSection(props){

const [audio, setAudio] = useState({
    audioURL: null,
    audioDetails: {
      url: null,
      blob: null,
      chunks: null,
      duration: {
        h: 0,
        m: 0,
        s: 0
      }
    }});

function customHandleAudioStop(data) {
    console.log(data);
    setAudio({
        ...audio,
        audioDetails:data
    });
}
function customHandleRest() {
    const reset = {
      url: null,
      blob: null,
      chunks: null,
      duration: {
        h: 0,
        m: 0,
        s: 0
      }
    };
    setAudio({
        ...audio,
        audioDetails:reset
    });
}
function customHandleAudioUpload(file) {
    console.log(file);
}
    return (
            <div className="Recorder">
              <Recorder
                record={true}
                title={"New recording"}
                audioURL={audio.audioDetails.url}
                showUIAudio
                handleAudioStop={(data) => customHandleAudioStop(data)}
                handleAudioUpload={(data) => customHandleAudioUpload(data)}
                //handleReset={(data) => customHandleRest()}
              />
            </div>
          );
        }



export default RecorderSection;
