import "./WelcomePage.css";
import { Link, useNavigate } from "react-router-dom";
import {Navigate} from "react-router-dom";
import { signInWithGoogle,auth } from "../db/firebase";
import HomePage from "../homepage/HomePage";
import { useAuthState } from "react-firebase-hooks/auth";
import React from "react";
import firebase from "../db/firebase";

function Login() {
  return (
    <Link to="/home" className="nav-link">
      <div>
        <button className="button" onClick={signInWithGoogle}>
          <i className="fab fa-google"></i>Sign in with google
        </button>
      </div>
    </Link>
  );
}

function LandingFrameMessage() {
  const [user, setUser] = React.useState(null);
  let navigate = useNavigate();
  React.useEffect(() => {
    firebase.auth().onAuthStateChanged((user) => {
      setUser(user);
      console.log(user);
    });
    if(user) navigate("/home");
  }, [navigate, setUser, user]);
  return (
    <div className="Landing-Frame-Message">
      <div className="Landing-Title">Speech to Text</div>

      <div className="Landing-description">
        Using FireBase to login and record user voice.
      </div>
      <br />
      {/* {user ? (
        <Navigate
          to={{
            pathname: "/home",
            user,
          }}
        />
      ) : (
        <Login />
      )} */}
      <Login />
    </div>
  );
}

function LandingFrame() {
  return (
    <div
      className="Landing-Frame"
      style={{
        backgroundImage: "url('/images/landing-page-background.jpg')",
      }}
    >
      <LandingFrameMessage />
    </div>
  );
}
function WelcomePage() {
  return (
    <div>
      <LandingFrame />
      <br />
    </div>
  );
}
export default WelcomePage;
