import "./WelcomePage.css";
import { Link } from "react-router-dom";
import { signInWithGoogle } from "../db/firebase.js";
import HomePage from "../homepage/HomePage";
import React from "react";
import firebase from "../db/firebase";

function Login() {
  return (
    <Link to="/home" className="nav-link">
      <div>
        <button className="button" onClick={signInWithGoogle}>
          <i className="fab fa-google"></i>Sign in with google
        </button>
      </div>
    </Link>
  );
}

function LandingFrameMessage({user}) {
  return (
    <div className="Landing-Frame-Message">
      <div className="Landing-Title">Speech to Text</div>

      <div className="Landing-description">
        Using FireBase to login and record user voice.
      </div>
      <br />
      {user ?<HomePage user={user}/>:<Login />}
    </div>
  );
}

function LandingFrame({user}) {
  return (
    <div
      className="Landing-Frame"
      style={{
        backgroundImage: "url('/images/landing-page-background.jpg')",
      }}
    >
      <LandingFrameMessage user={user}/>
    </div>
  );
}
function WelcomePage() {
  const [user, setUser] = React.useState(null);

  React.useEffect(() => {
    firebase.auth().onAuthStateChanged(user => {
      setUser(user);
    })
  }, [])
  return (
    <div>
      <LandingFrame />
      <br />
    </div>
  );
}
export default WelcomePage;
