import "./HomePage.css";
import { useNavigate } from "react-router-dom";
import { useAuth ,firebaseApp,useProvideAuth} from "../db/firebase";
import RecorderSection from "../recordpage/Recorder";
function LandingFrameMessage(props) {
  const auth = useAuth();
  const {  loading,
    user,
    signin,
    signout,} = useProvideAuth();



  const navigate = useNavigate();
  console.log(auth);
  return (
    <div className="Landing-Frame-Message">
      <div className="Landing-Title">
        <h1>
          Hello, <span></span>
          {user.displayName}
        </h1>
      </div>
      <br />
      <div className="Landing-description">
        <img src={user.photoURL} alt="" />
        <br />
        <button
          className="button signout"
          onClick={() => {
            auth
              .signout()
              .then(() => {
                console.log("Sign out succesful");
                navigate("/");
              })
              .catch((error) => console.error(error));
          }}
        >
          Sign out
        </button>
      </div>
      {/* <RecorderSection /> */}
    </div>

  );
}

function LandingFrame(props) {
  return (
    <div
      className="Landing-Frame"
      style={{
        backgroundImage: "url('/images/landing-page-background.jpg')",
      }}
    >
      <LandingFrameMessage props={props} />
      <br />

    </div>
  );
}
function HomePage(props) {
  return (
    <div>
      <LandingFrame props={props} />
      <br />
    </div>
  );
}
export default HomePage;
