import "./WelcomePage.css";
import { Link, useNavigate } from "react-router-dom";
import {Navigate} from "react-router-dom";
import { signInWithGoogle,auth, useAuth } from "../db/firebase";
import HomePage from "../homepage/HomePage";
import { useAuthState } from "react-firebase-hooks/auth";
import React from "react";
import firebase from "../db/firebase";



const Login = () => {
  const auth = useAuth();
  const navigate = useNavigate();

  const onLogin = () => {
    auth
      .signin()
      .then(() => {
        console.log("Login succesful");
        navigate("/");
      })
      .catch((error) => console.error(error));
  };
  return <button onClick={onLogin}>Login</button>;
};



function LoginButton() {
  return (
    <Link to="/home" className="nav-link">
      <div>
        <button className="button" onClick={Login}>
          <i className="fab fa-google"></i>Sign in with google
        </button>
      </div>
    </Link>
  );
}

function LandingFrameMessage() {
  const auth =useAuth();
  return (
    <div className="Landing-Frame-Message">
      <div className="Landing-Title">Speech to Text</div>

      <div className="Landing-description">
        Using FireBase to login and record user voice.
      </div>
      <br />
      <LoginButton />
    </div>
  );
}

function LandingFrame() {
  return (
    <div
      className="Landing-Frame"
      style={{
        backgroundImage: "url('/images/landing-page-background.jpg')",
      }}
    >
      <LandingFrameMessage />
    </div>
  );
}
function WelcomePage() {
  return (
    <div>
      <LandingFrame />
      <br />
    </div>
  );
}
export default WelcomePage;
